library(tidyverse)
library(gt)
library(scales)
olympics_full &lt;- read_csv("olympics.csv")
olympics &lt;- olympics_full |&gt;
  filter(!is.na(medal)) |&gt;
  separate_wider_delim(cols = team, names = c("team", "suffix"), delim = "-", too_many = "merge", too_few = "align_start") |&gt;
  select(-suffix) |&gt;
  mutate(medal = fct_relevel(medal, "Bronze", "Silver", "Gold"))
